package pages;import configs.browserSelector;import configs.loadProps;import configs.utils;import org.testng.annotations.AfterTest;import org.testng.annotations.BeforeTest;import org.testng.annotations.Parameters;import org.testng.annotations.Test;import java.io.IOException;public class login extends utils {    public static String U_name = loadProps.getProperty("Username");    public static String P_word = loadProps.getProperty("Password");    public static String URL = loadProps.getProperty("URL");    public static String InValid_Password = generateRandomText(8);    public static String username = "//input[@id='Username']";    public static String password = "//input[@id='Password']";    public static String SignInButton = "//button[@name='button']";    public static String UserIcon = "//span[@class='menu_user_title']";    public static String logoutButton = "//*[@id=\"Logout\"]/li[1]/a/div/span";    @Test    public static void login() throws IOException {        if (page.isVisible(username)) {            clickOnElement(username);            enterText(username, U_name);            clickOnElement(password);            enterText(password, P_word);            clickOnElement(SignInButton);            System.out.println("User Logged in.");            logout();        } else {            System.out.println("User Already Logged in");        }    }    public static void Failed_login() {        clickOnElement(username);        enterText(username, U_name);        clickOnElement(password);        enterText(password, InValid_Password);        clickOnElement(SignInButton);    }    public static void logout() {        if (isElementPresent(logoutButton)) {            clickOnElement(logoutButton);        } else {            page.navigate(URL);        }    }    @AfterTest    public void closeBrowser() throws Exception {        browserSelector.closeBrowser();    }    @BeforeTest    @Parameters("Browsers")    public void launchBrowser(String Browser) throws Exception {        browserSelector.LaunchBrowser(Browser);    }}